mapper.insert:
INSERT INTO MATERIAL
    (
    MATERIAL_NM,
    MATERIAL_CLASSIFICATION
    )
VALUES
    (
    #{matNm},
    #{matDiv}
    )

INSERT INTO SUPPLIER
    (
    SUP_NAME
    , PHONE_NUM
    , SUP_ADDRESS
    , SUP_EMAIL
    , SUP_SELL)
VALUES
    (
    #{supNm}
    , #{supContact}
    , #{supAddress}
    , #{supEmail}
    , #{supSell}
    )

INSERT INTO BOM
    (BOM_ID
    , PRODUCT_ID
    , MATERIAL_ID
    , LOT_ID
    , BOM_PROD_QUANTITY
    , QUANTITY_UNITS
    )
VALUES (
    (SELECT DISTINCT b.BOM_ID as bomId
        FROM BOM b
        WHERE b.PRODUCT_ID =
        (
        SELECT fp.product_id as prodId
        FROM FINISHED_PRODUCT fp
        WHERE fp.PRODUCT_NM = #{prodNm}
        AND fp.PRODUCT_SPEC = #{prodSpec}
        )
    )
    , (
    SELECT fp.product_id as prodId
        FROM FINISHED_PRODUCT fp
        WHERE fp.PRODUCT_NM = #{prodNm}
        AND fp.PRODUCT_SPEC = #{prodSpec}
    )
    , (
    SELECT m.MATERIAL_ID as matId
        FROM MATERIAL m
        WHERE m.MATERIAL_NM = #{matNm}
    )
    , (
    SELECT i.LOT_ID as lotId
        FROM INSTRUCTION i
        WHERE i.PRODUCT_ID =
            (
            SELECT fp.product_id as prodId
            FROM FINISHED_PRODUCT fp
            WHERE fp.PRODUCT_NM = #{prodNm}
            AND fp.PRODUCT_SPEC = #{prodSpec}
            )
    )
    , #{bomProdQuantity} * (SELECT i.LOT_SIZE as lotSize
            FROM INSTRUCTION i
            WHERE i.PRODUCT_ID =
            (
            SELECT fp.product_id as prodId
                FROM FINISHED_PRODUCT fp
                WHERE fp.PRODUCT_NM = #{prodNm}
                AND fp.PRODUCT_SPEC = #{prodSpec}
                )
    )
    , #{quantityUnits}
    )

INSERT INTO BOM
    (PRODUCT_ID
    , MATERIAL_ID
    , LOT_ID
    , BOM_PROD_QUANTITY
    , QUANTITY_UNITS
    )
VALUES (
    (
    SELECT fp.product_id as prodId
    FROM FINISHED_PRODUCT fp
    WHERE fp.PRODUCT_NM = #{prodNm}
    AND fp.PRODUCT_SPEC = #{prodSpec}
    )
    , (
    SELECT m.MATERIAL_ID as matId
    FROM MATERIAL m
    WHERE m.MATERIAL_NM = #{matNm}
    )
    , (
    SELECT i.LOT_ID as lotId
    FROM INSTRUCTION i
    WHERE i.PRODUCT_ID =
    (
    SELECT fp.product_id as prodId
    FROM FINISHED_PRODUCT fp
    WHERE fp.PRODUCT_NM = #{prodNm}
    AND fp.PRODUCT_SPEC = #{prodSpec}
    )
    )
    , #{bomProdQuantity} * (SELECT i.LOT_SIZE as lotSize
    FROM INSTRUCTION i
    WHERE i.PRODUCT_ID =
    (
    SELECT fp.product_id as prodId
    FROM FINISHED_PRODUCT fp
    WHERE fp.PRODUCT_NM = #{prodNm}
    AND fp.PRODUCT_SPEC = #{prodSpec}
    )
    )
    , #{quantityUnits}
    )

mapper.delete:
DELETE FROM BOM
WHERE bom_id = #{bomId}
AND material_id = #{matId}

mapper.update:
BEGIN
    UPDATE FINISHED_PRODUCT
        SET
            PRODUCT_DIV = #{editData5},
            PRODUCT_SPEC = #{editData6}
        WHERE
            PRODUCT_ID = #{editData3}
    ;

    UPDATE MATERIAL
        SET
            MATERIAL_CLASSIFICATION = #{editData9}
        WHERE
            MATERIAL_ID = #{editData10}
    ;

    UPDATE INSTRUCTION
        SET
            LOT_SIZE = #{editData8}
        WHERE
            LOT_ID = #{editData7}
    ;

    UPDATE BOM
        SET
            BOM_PROD_QUANTITY = #{editData13},
            QUANTITY_UNITS = #{editData12}
        WHERE
            BOM_ID = #{editData2}
        AND PRODUCT_ID = #{editData3}
        AND MATERIAL_ID = #{editData10}
        AND LOT_ID = #{editData7}
    ;

    COMMIT;
END;
